const INPUT_RAW = ``;

console.log('Day 6, Part 1');
for(let i = 0; i < INPUT_RAW.length; i++)
{
	// get the next four characters in the sequence
	let chars = [
		INPUT_RAW[i],
		INPUT_RAW[i+1],
		INPUT_RAW[i+2],
		INPUT_RAW[i+3]
	];

	if(setIsUnique(chars))
	{
		// include the current string in the number of characters checked
		console.log(i+4);
		break;
	}
}

