function setIsUnique(set)
{
	// cross-reference each character in the set which each other character
	for(let j = 0; j < set.length; j++)
	{
		for(let k = 0; k < set.length; k++)
		{
			// leave early if there's a repeated character (and it's not itself)
			if(j !== k && set[j] === set[k])
				return false;
		}
	}

	return true;
}